import{_ as s,o as a,c as n,O as e}from"./chunks/framework.a9f788d3.js";const A=JSON.parse('{"title":"apt 配置","description":"","frontmatter":{},"headers":[],"relativePath":"modules/system/ubuntu/apt使用.md"}'),l={name:"modules/system/ubuntu/apt使用.md"},t=e(`<h1 id="apt-配置" tabindex="-1">apt 配置 <a class="header-anchor" href="#apt-配置" aria-label="Permalink to &quot;apt 配置&quot;">​</a></h1><h2 id="镜像修改" tabindex="-1">镜像修改 <a class="header-anchor" href="#镜像修改" aria-label="Permalink to &quot;镜像修改&quot;">​</a></h2><p>apt初始源在国内很慢, 因此需要修改为国内的镜像地址, 下方是对应版本 20.04 的镜像地址:</p><div class="language-txt"><button title="Copy Code" class="copy"></button><span class="lang">txt</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">deb https://mirrors.aliyun.com/ubuntu/ focal main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;">deb-src https://mirrors.aliyun.com/ubuntu/ focal main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">deb https://mirrors.aliyun.com/ubuntu/ focal-security main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;">deb-src https://mirrors.aliyun.com/ubuntu/ focal-security main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">deb https://mirrors.aliyun.com/ubuntu/ focal-updates main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;">deb-src https://mirrors.aliyun.com/ubuntu/ focal-updates main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"># deb https://mirrors.aliyun.com/ubuntu/ focal-proposed main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;"># deb-src https://mirrors.aliyun.com/ubuntu/ focal-proposed main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;">deb https://mirrors.aliyun.com/ubuntu/ focal-backports main restricted universe multiverse</span></span>
<span class="line"><span style="color:#A6ACCD;">deb-src https://mirrors.aliyun.com/ubuntu/ focal-backports main restricted universe multiverse</span></span></code></pre></div><p>修改文件位置: <code>/etc/apt/sources.list</code>. 写入上方内容即可.</p><h2 id="常用命令" tabindex="-1">常用命令 <a class="header-anchor" href="#常用命令" aria-label="Permalink to &quot;常用命令&quot;">​</a></h2><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">update</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 更新软件源</span></span>
<span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">list</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--installed</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 查看已安装软件</span></span>
<span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">package_name</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 安装软件</span></span>
<span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">upgrade</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">package_name</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 升级软件</span></span>
<span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">remove</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">package_name</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 卸载软件</span></span>
<span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">autoremove</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">package_name</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># 卸载软件及依赖</span></span></code></pre></div>`,7),p=[t];function o(r,c,i,u,C,y){return a(),n("div",null,p)}const d=s(l,[["render",o]]);export{A as __pageData,d as default};
